// Tailwind CSS JavaScript utilities
window.tailwind = {
  // Utility functions for dynamic class management
  addClass: function(element, className) {
    if (element && className) {
      element.classList.add(className);
    }
  },
  
  removeClass: function(element, className) {
    if (element && className) {
      element.classList.remove(className);
    }
  },
  
  toggleClass: function(element, className) {
    if (element && className) {
      element.classList.toggle(className);
    }
  },
  
  hasClass: function(element, className) {
    if (element && className) {
      return element.classList.contains(className);
    }
    return false;
  },
  
  // Responsive utilities
  getBreakpoint: function() {
    const width = window.innerWidth;
    if (width >= 1536) return 'xl';
    if (width >= 1280) return 'xl';
    if (width >= 1024) return 'lg';
    if (width >= 768) return 'md';
    if (width >= 640) return 'sm';
    return 'xs';
  },
  
  // Animation utilities
  animate: function(element, animation, duration = 300) {
    if (element) {
      element.style.transition = `all ${duration}ms ease-in-out`;
      if (animation === 'fadeIn') {
        element.style.opacity = '0';
        setTimeout(() => element.style.opacity = '1', 10);
      } else if (animation === 'fadeOut') {
        element.style.opacity = '1';
        setTimeout(() => element.style.opacity = '0', 10);
      } else if (animation === 'slideUp') {
        element.style.transform = 'translateY(20px)';
        setTimeout(() => element.style.transform = 'translateY(0)', 10);
      } else if (animation === 'slideDown') {
        element.style.transform = 'translateY(-20px)';
        setTimeout(() => element.style.transform = 'translateY(0)', 10);
      }
    }
  },
  
  // Theme utilities
  setTheme: function(theme) {
    document.documentElement.setAttribute('data-theme', theme);
  },
  
  getTheme: function() {
    return document.documentElement.getAttribute('data-theme') || 'light';
  },
  
  // Color utilities
  colors: {
    primary: '#3b82f6',
    secondary: '#6b7280',
    success: '#10b981',
    warning: '#f59e0b',
    error: '#ef4444',
    info: '#06b6d4'
  }
};

// Initialize Tailwind utilities on DOM ready
document.addEventListener('DOMContentLoaded', function() {
  // Add responsive classes based on screen size
  function updateResponsiveClasses() {
    const breakpoint = window.tailwind.getBreakpoint();
    document.body.setAttribute('data-breakpoint', breakpoint);
  }
  
  updateResponsiveClasses();
  window.addEventListener('resize', updateResponsiveClasses);
  
  // Initialize hover effects
  document.querySelectorAll('[class*="hover:"]').forEach(element => {
    element.addEventListener('mouseenter', function() {
      this.style.transition = 'all 0.2s ease-in-out';
    });
  });
  
  // Initialize focus effects
  document.querySelectorAll('[class*="focus:"]').forEach(element => {
    element.addEventListener('focus', function() {
      this.style.transition = 'all 0.2s ease-in-out';
    });
  });
});

console.log('Tailwind CSS JavaScript utilities loaded successfully!');